const Tours = () => {
return (
  <>
     <CommonSection title={'Tours'} />
      <section>
        <Container>
            <Row>
              <SearchBar />
            </Row>
          </Container>
          </section>
     
          <Newsletter />
          </>
      );    
    };
export default Tours;
